import * as React from 'react';
import AppBar from '@mui/material/AppBar';
import Button from '@mui/material/Button';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import CssBaseline from '@mui/material/CssBaseline';
import Grid from '@mui/material/Grid';
import Stack from '@mui/material/Stack';
import Box from '@mui/material/Box';
import {  Paper, TextField } from '@mui/material';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import Link from '@mui/material/Link';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { useEffect, useState } from "react";
import myApi from "../../Api";
import useInput from "../../Validator";
import "../../custom.css";
import { useNavigate } from "react-router-dom"  ;
import CustomizedAccordions from './Accordion';
import BasicSelect from './Select';

const Alert = React.forwardRef(function Alert(props, ref) {
  return <MuiAlert elevation={6} ref={ref} variant="filled" {...props} />;
});



function Main() {
  const navigate = useNavigate();
  const [opros, setOpros] = useState("");
  let FIO = useInput("", { isEmpty: true, FIO: false });
  let age = useInput("", { isEmpty: true, age: false });
  let email = useInput("", { isEmpty: true, isEmail: false });
  let phone = useInput("", { isEmpty: true, phone: false });
  let location = useInput("", { isEmpty: true, location: false });
  let formObject = {
    name: FIO.formValue,
    email: email.formValue,
    age: age.formValue,
    phone: phone.formValue,
    location: location.formValue,
    opros: opros,
  };


  const handleChange = (e) => {
    let { name, value } = e.target;

    console.log(opros);
    setOpros(value);
  };
  const handleSubmit = (evt) => {
    setOpen(true);
    window.location.reload();
    myApi.postUser(formObject);
    myApi.postEmail({
      email: email.formValue,
      text: `Здравствуйте, ваша заявка успешно принята принята! Ожидайте обратной связи`,
    });

  };
  return (

    <div>
      <ThemeProvider theme={defaultTheme}>
      <CssBaseline />
      <main>
        {/* Hero unit */}
        <Box
          sx={{
            bgcolor: 'background.paper',
            pt: 8,
            pb: 6,
          }}
        >
          <Container maxWidth="sm">
            <Typography
              component="h1"
              variant="h2"
              align="center"
              color="text.primary"
              gutterBottom
            >
              Album layout
            </Typography>
            <Typography variant="h5" align="center" color="text.secondary" paragraph>
              Something short and leading about the collection below—its contents,
              the creator, etc. Make it short and sweet, but not too short so folks
              don&apos;t simply skip over it entirely.
            </Typography>
            <Stack
              sx={{ pt: 4 }}
              direction="row"
              spacing={2}
              justifyContent="center"
            >
              <Button variant="contained">Main call to action</Button>
              <Button variant="outlined">Secondary action</Button>
            </Stack>
          </Container>
        </Box>
        <Container sx={{ py: 8 }} maxWidth="md">
          <Grid container spacing={4}>
              <Grid item xs={12} sm={6} md={4}>
                <Card
                  sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}
                >
                  <CardMedia
                    component="div"
                    sx={{
                      // 16:9
                      pt: '56.25%',
                    }}
                    image="https://source.unsplash.com/random?wallpapers"
                  />
                  <CardContent sx={{ flexGrow: 1 }}>
                    <Typography gutterBottom variant="h5" component="h2">
                      Heading
                    </Typography>
                    <Typography>
                      This is a media card. You can use this section to describe the
                      content.
                    </Typography>
                  </CardContent>
                  <CardActions>
                    <Button size="small">View</Button>
                    <Button size="small">Edit</Button>
                  </CardActions>
                </Card>
              </Grid>
          </Grid>
        </Container>
        <Container maxWidth="md" style={{ marginTop: '20px' }}>
        <Grid container spacing={3}>
          <Grid item xs={12}>
            <Paper style={{ padding: 20 }}>
              <Typography variant="h5" gutterBottom>
                Оставьте заявку на контрактную службу
              </Typography>
              <form onSubmit={handleSubmit}>
                <Grid container spacing={2}>
                  <Grid item xs={12}>
                    <TextField label="ФИО" variant="outlined" fullWidth
                    onChange={(e) => FIO.onChange(e)}
                    onBlur={(e) => FIO.onBlur(e)}
                    value={FIO.value}
                    name="FIO"
                    type="text"
                    />
                  </Grid>
                  <Grid item xs={12}>
                    <TextField label="Возраст" variant="outlined" fullWidth 
                    onChange={(e) => age.onChange(e)}
                    onBlur={(e) => age.onBlur(e)}
                    value={age.value}
                    name="age"
                    type="text"
                    />
                  </Grid>
                  <Grid item xs={12}>
                    <TextField label="Электронная почта" variant="outlined" fullWidth 
                    onChange={(e) => email.onChange(e)}
                    onBlur={(e) => email.onBlur(e)}
                    value={email.value}
                    name="email"
                    type="text"
                    />
                  </Grid>
                  <Grid item xs={12}>
                    <TextField label="Номер телефона" variant="outlined" fullWidth 
                    onChange={(e) => phone.onChange(e)}
                    onBlur={(e) => phone.onBlur(e)}
                    value={phone.value}
                    name="phone"
                    type="text"
                   
                    />
                  </Grid>
                  <Grid item xs={12}>
                    <TextField label="Место жительства" variant="outlined" fullWidth 
                    onChange={(e) => location.onChange(e)}
                    onBlur={(e) => location.onBlur(e)}
                    value={location.value}
                    name="location"
                    type="text"
        
                    />
                  </Grid>
                  <Grid item xs={12}><BasicSelect onChange={handleChange}/></Grid>
                  <Grid item xs={12}>
                    <Button variant="contained" color="primary" type="submit"
                    /*disabled={
                      FIO.FIOError ||
                      age.ageError ||
                      email.isEmailError ||
                      phone.phoneError ||
                      location.locationError
                        ? true
                        : false}*/
                    >
                      Отправить заявку
                    </Button>
                    
                  </Grid>
                </Grid>
              </form>
            </Paper>
          </Grid>
          <Grid item xs={12}>
            <Paper style={{ padding: 20 }}>
              <Typography variant="h5" gutterBottom>
                Информационный блок
              </Typography>
              <Typography variant="body1">
                Здесь можно разместить информацию о контрактной службе, политике конфиденциальности, контактные данные и другую важную информацию.
              </Typography>
            </Paper>
          </Grid>
          <Grid item xs={12}>
            <Paper style={{ padding: 20 }}>
              <Typography variant="h5" gutterBottom>
                Контакты
              </Typography>
              <Typography variant="body1">
                Телефон: 8-800-555-35-35 <br />
                Email: info@example.com <br />
                Адрес: ул. Пушкина, дом Колотушкина
              </Typography>
            </Paper>
          </Grid>
        </Grid>
      </Container>
      <Container>
        <Grid>
        <Paper style={{ padding: 20 }}>
          <CustomizedAccordions/>
        </Paper>
        </Grid>
      </Container>
      </main>
      {/* Footer */}
      <Box sx={{ bgcolor: 'background.paper', p: 6 }} component="footer">
        <Typography variant="h6" align="center" gutterBottom>
          Footer
        </Typography>
        <Typography
          variant="subtitle1"
          align="center"
          color="text.secondary"
          component="p"
        >
          Something here to give the footer a purpose!
        </Typography>
      </Box>
      {/* End footer */}
    </ThemeProvider>
    </div>

  );
}

export default Main;
