import { useEffect, useState } from "react";
import myApi from "../../Api";

export const Admin = () => {
  const [users, setUsers] = useState([]);
  const [text, setText] = useState("");
  useEffect(() => {
    myApi
      .apiGet()
      .then((data) => {
        setUsers(data);
      })
      .catch((err) => {
        console.error(err);
      });
  }, []);
  return (
    <>
     
        {users.map((u) => (
            <div>
             <form
             onSubmit={(evt)=>{window.location.reload();setText('');
             return text !=='' && myApi.postEmail({ email: u.email, text: `${text}` })}}
           >
          
            <span>{u.name}</span>
            <input className="chlen" onInput={(data)  =>{ setText(data.target.value)}}></input>
            <button className="button"   type="submit">Отправить</button>
            <span>{u.email}</span> <span>{u.phone}</span>
            <button
              onClick={() => {
                myApi.deleteUser(u.id).then((data) => {
                  setUsers((data) => data.filter((c) => c.id !== u.id));
                });
              }}
            >
              Удалить
            </button>
            </form>
          </div>
        ))}
     
    </>
  );
};
