import * as React from "react";
import Button from "@mui/material/Button";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import CssBaseline from "@mui/material/CssBaseline";
import Grid from "@mui/material/Grid";
import Stack from "@mui/material/Stack";
import Box from "@mui/material/Box";
import { Paper, TextField } from "@mui/material";
import Typography from "@mui/material/Typography";
import Container from "@mui/material/Container";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import { useEffect, useState } from "react";
import myApi from "../../Api";
import useInput from "../../Validator";
import "../../custom.css";
import CustomizedAccordions from "./Accordion";
import BasicSelect from "./Select";
import Checkbox from "@mui/material/Checkbox";
import fon from "../../img/fon.jpg"
import InteractiveList from "./List";
import AlertDialogSlide from "./Dialog";
import { createStyles, makeStyles } from '@mui/styles';

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      backgroundColor: theme.palette.red,
    },
  }),
);
const defaultTheme = createTheme();
function Main() {
  const label = { inputProps: { "aria-label": "Checkbox demo" } };
  const [opros, setOpros] = useState("");
  let FIO = useInput("", { isEmpty: true, FIO: false });
  let age = useInput("", { isEmpty: true, age: false });
  let email = useInput("", { isEmpty: true, isEmail: false });
  let phone = useInput("", { isEmpty: true, phone: false });
  let location = useInput("", { isEmpty: true, location: false });
  let formObject = {
    name: FIO.formValue,
    email: email.formValue,
    age: age.formValue,
    phone: phone.formValue,
    location: location.formValue,
    opros: opros,
  };

  const handleChange = (e) => {
    let { name, value } = e.target;

    console.log(opros);
    setOpros(value);
  };
  const handleSubmit = (evt) => {
    evt.preventDefault();
    window.location.reload();
    myApi.postUser(formObject);
    myApi.postEmail({
      email: email.formValue,
      text: `Здравствуйте, ваша заявка успешно принята принята! Ожидайте обратной связи`,
    });
  };
  return (
    <div>

      <ThemeProvider theme={defaultTheme}>
        <CssBaseline />
        <main>
          <Box
            sx={{
              bgcolor: "background.",
              pt: 8,
              pb: 6,
            }}
          >
            <Container maxWidth="ms" sx={{
                  backgroundImage: '',
                  backgroundRepeat: 'no-repeat',
                  backgroundSize: 'cover',
                  backgroundPosition: 'center',
                }}>
              <Typography
                component="h1"
                variant="h2"
                align="center"
                color="text.primary"
                gutterBottom
              >
                Служба по контракту
              </Typography>
              <Typography
                variant="h5"
                align="center"
                color="text.secondary"
                paragraph
              >
                 В ПОГРАНИЧНОМ УПРАВЛЕНИИ ФСБ РОССИИ ПО РЕСПУБЛИКЕ КАЛМЫКИЯ И
            АСТРАХАНСКОЙ ОБЛАСТИ
              </Typography>
              <Stack
                sx={{ pt: 4 }}
                direction="row"
                spacing={2}
                justifyContent="center"
              >
              </Stack>
            </Container>
          </Box>
          <Container sx={{ py: 8 }} maxWidth="md">
            <Grid container spacing={4}>
              <Grid item xs={12} sm={6} md={4}>
                <Card
                  sx={{
                    height: "100%",
                    display: "flex",
                    flexDirection: "column",
                  }}
                >
                  <CardMedia
                    component="div"
                    sx={{
                      // 16:9
                      pt: "56.25%",
                    }}
                    image="https://source.unsplash.com/random?wallpapers"
                  />
                  <CardContent sx={{ flexGrow: 1 }}>
                    <Typography gutterBottom variant="h5" component="h2">
                      Heading
                    </Typography>
                    <Typography>
                      This is a media card. You can use this section to describe
                      the content.
                    </Typography>
                  </CardContent>
                </Card>
              </Grid>
            </Grid>
            <Grid item xs={12}>
                <Paper style={{ padding: 20 }}>
                  <Typography variant="h5" gutterBottom>
                    Информационный блок
                  </Typography>
                  <Typography variant="body1">
                    <InteractiveList/>
                  </Typography>
                </Paper>
              </Grid>
          </Container>
          <Container maxWidth="md" style={{ marginTop: "20px" }}>
            <Grid container spacing={3}>
              <Grid item xs={12}>
                <Paper style={{ padding: 20 }}>
                  <Typography variant="h5" gutterBottom align="center">
                    Оставьте заявку на контрактную службу
                  </Typography>
                  <form onSubmit={handleSubmit}>
                    <Grid container spacing={2}>
                      <Grid item xs={12}>
                     
                        <TextField
                          variant="outlined"
                          fullWidth
                          label={FIO.isDirty && FIO.FIOError ? "Некорректно заполнено поле ФИО" : "ФИО"}
                          error={FIO.isDirty && FIO.FIOError ? true : false}
                          onChange={(e) => FIO.onChange(e)}
                          onBlur={(e) => FIO.onBlur(e)}
                          value={FIO.value}
                          name="FIO"
                          type="text"
                        />
                      </Grid>
                      <Grid item xs={12}>
                        <TextField
                          label={age.isDirty && age.ageError ? "Некорректно заполнено поле возраст" : "Возраст"}
                          error={age.isDirty && age.ageError ? true : false}
                          variant="outlined"
                          fullWidth
                          onChange={(e) => age.onChange(e)}
                          onBlur={(e) => age.onBlur(e)}
                          value={age.value}
                          name="age"
                          type="text"
                        />
                      </Grid>
                      <Grid item xs={12}>
                        <TextField
                          label={email.isDirty && email.isEmailErrorr ? "Некорректно заполнено поле электронная почта'" : "Электронная почта"}
                          error={email.isDirty && email.isEmailErrorr ? true : false}
                          variant="outlined"
                          fullWidth
                          onChange={(e) => email.onChange(e)}
                          onBlur={(e) => email.onBlur(e)}
                          value={email.value}
                          name="email"
                          type="text"
                        />
                      </Grid>
                      <Grid item xs={12}>
                        <TextField
                          label={phone.isDirty && phone.phoneError ? "Некорректно заполнено поле номер телефона" : "Номер телефона"}
                          error={phone.isDirty && phone.phoneError ? true : false}
                          variant="outlined"
                          fullWidth
                          onChange={(e) => phone.onChange(e)}
                          onBlur={(e) => phone.onBlur(e)}
                          value={phone.value}
                          name="phone"
                          type="text"
                        />
                      </Grid>
                      <Grid item xs={12}>
                        <TextField
                          label={location.isDirty && location.locationError ? "Некорректно заполнено поле место жительства" : "Место жительства"}
                          error={location.isDirty && location.locationError ? true : false}
                          variant="outlined"
                          fullWidth
                          onChange={(e) => location.onChange(e)}
                          onBlur={(e) => location.onBlur(e)}
                          value={location.value}
                          name="location"
                          type="text"
                        />
                      </Grid>
                      <Grid item xs={12}>
                        <BasicSelect onChange={handleChange} />
                      </Grid>
                      <Grid item xs={7}>
                        <Checkbox {...label} />
                        <AlertDialogSlide/>
                      </Grid>
                      <Grid item xs={12}>
                        <Button
                          variant="contained"
                          color="primary"
                          type="submit"
                          disabled={
                      FIO.FIOError ||
                      age.ageError ||
                      email.isEmailError ||
                      phone.phoneError ||
                      location.locationError
                        ? true
                        : false}
                        >
                          Отправить заявку
                        </Button>
                      </Grid>
                    </Grid>
                  </form>
                </Paper>
              </Grid>
              <Grid item xs={12}>
                <Paper style={{ padding: 20 }}>
                  <Typography variant="h5" gutterBottom>
                    Контакты
                  </Typography>
                  <Typography variant="body1">
                    Телефон: 8-800-555-35-35 <br />
                    Email: info@example.com <br />
                    Адрес: ул. Пушкина, дом Колотушкина
                  </Typography>
                </Paper>
              </Grid>
            </Grid>
          </Container>
          <Container>
            <Grid>
              <Paper style={{ padding: 20 }}>
                <CustomizedAccordions />
              </Paper>
            </Grid>
          </Container>
        </main>
        {/* Footer */}
        <Box sx={{ bgcolor: "background.paper", p: 6 }} component="footer">
          <Typography variant="h6" align="center" gutterBottom>
            Footer
          </Typography>
          <Typography
            variant="subtitle1"
            align="center"
            color="text.secondary"
            component="p"
          >
            Something here to give the footer a purpose!
          </Typography>
        </Box>
      </ThemeProvider>
    </div>
  );
}

export default Main;
