import { useEffect, useState } from "react";
import myApi from "../../Api";
import './Admin.css'
export const Admin = () => {
  const [users, setUsers] = useState([]);
  const [text, setText] = useState("");
  const [counter,setCounter]=useState(null)
  useEffect(() => {
    myApi
      .apiGet()
      .then((data) => {
        setUsers(data);
      })
      .catch((err) => {
        console.error(err);
      });
      
  }, []);


  let counter1=0
  useEffect(()=>{setCounter(counter1)
  },[counter1])
  
  return (
   
     <div className="admin">
        {users.map((u) =>{
          
        if(u.opros=="10"){
          counter1+=1;
          return counter1
        }
         
        return(
            <div className="admin__cell">
             <form className="justify-content-center"
             onSubmit={(evt)=>{window.location.reload();setText('');
             return text !=='' && myApi.postEmail({ email: u.email, text: `${text}` })}}
           >
            <span>{u.name}</span>
            <span>{u.age}</span>
            <span>{u.email}</span>
            <span>{u.phone}</span>
            <span>{u.location}</span>
            <input className="chlen" onInput={(data)  =>{ setText(data.target.value)}}></input>
            <button className="button"   type="submit">Отправить</button>
            <button
              onClick={() => {
                myApi.deleteUser(u.id).then((data) => {
                  setUsers((data) => data.filter((c) => c.id !== u.id));
                });
              }}
            >
              Удалить
            </button>
            </form>
          </div>
        )})}
     <div>
    
     </div>
     <span>{counter}</span>
     </div>
    
    
  );
};
